// Extract category URL from Google Sheets
const items = $input.all();

console.log('üìä Total items from sheet:', items.length);

if (items.length === 0) {
  throw new Error('‚ùå No data in URL-CAT sheet. Add category URLs first.');
}

// Get first item
const firstItem = items[0].json;

// Try multiple field name variations
const categoryUrl = firstItem['category url'] || 
                   firstItem.category_url || 
                   firstItem.url || 
                   firstItem.A;

if (!categoryUrl) {
  console.error('Available fields:', Object.keys(firstItem));
  throw new Error('‚ùå Column "category url" not found in sheet');
}

const urlStr = String(categoryUrl).trim();

console.log('‚úÖ Category URL:', urlStr);

if (!urlStr.startsWith('http')) {
  throw new Error('‚ùå Invalid URL format. Must start with http:// or https://');
}

// Optional: Extract max_pages from sheet (column "max pages")
const maxPages = firstItem['max pages'] || firstItem.max_pages || null;

return { 
  json: { 
    category_url: urlStr,
    max_pages: maxPages ? parseInt(maxPages) : null
  } 
};

