parameters.jsCode:
// Log job start
const response = $input.first().json;

if (!response.success) {
  throw new Error('‚ùå Failed to start job: ' + (response.error || 'Unknown error'));
}

const jobId = response.job_id;
console.log('‚úÖ Job started! ID:', jobId);
console.log('üìä Category:', response.data.category_url);
console.log('üìÑ Max pages:', response.data.max_pages || 'ALL');

return [{
  json: {
    job_id: jobId,
    category_url: response.data.category_url,
    started_at: new Date().toISOString()
  }
}];

// Check job status and route accordingly
const data = $input.first().json;
const status = data.status;
const jobId = data.job_id;

console.log('üîç Job', jobId, 'status:', status);

if (status === 'completed') {
  console.log('‚úÖ Job completed successfully!');
  console.log('üìä Total lots scraped:', data.result?.total_lots || 0);
  // Output 1: Success
  return [[$input.first()], null, null];

} else if (status === 'running' || status === 'pending') {
  console.log('‚è≥ Job still running, will retry...');
  // Output 3: Retry
  return [null, null, [$input.first()]];

} else if (status === 'failed') {
  console.error('‚ùå Job failed!');
  console.error('Error:', data.error);
  // Output 2: Error
  return [null, [$input.first()], null];

} else {
  console.error('‚ö†Ô∏è Unknown status:', status);
  return [null, [$input.first()], null];
}

// Extract and format results for Google Sheets
const data = $input.first().json;
const results = data.result?.results || [];

if (results.length === 0) {
  throw new Error('‚ùå No results in completed job. Check API logs.');
}

console.log('üìä Processing', results.length, 'wine lots');

// Format each wine lot for CATALOG sheet
return results.map((item, index) => {
  console.log(`  ${index + 1}. ${item.title?.substring(0, 50)}...`);

  return {
    json: {
      title: item.title || '',
      bottles_count: item.bottles_count || '',
      seller_name: item.seller_name || '',
      current_price: item.current_price || '',
      shipping_cost: item.shipping_cost || '',
      end_date: item.end_date || '',
      images_count: (item.images || []).length,
      first_image: item.first_image || '',
      url: item.url || '',
      scraped_at: item.scraped_at || new Date().toISOString(),
      producer_rating: item.producer_rating || '',
      vintage_rating: item.vintage_rating || '',
      region_rating: item.region_rating || '',
      overall_appeal: item.overall_appeal || '',
      investment_potential: item.investment_potential || ''
    }
  };
});

const data = $input.first().json;
const jobId = data.job_id;
const error = data.error || 'Unknown error';

console.error('‚ùå Job', jobId, 'failed!');
console.error('Error:', error);

return [{
  json: {
    error: true,
    job_id: jobId,
    status: data.status,
    error_message: error,
    timestamp: new Date().toISOString()
  }
}];

// Check retry count to prevent infinite loops
const data = $input.first().json;
const retryCount = data.retry_count || 0;

if (retryCount >= 5) {
  console.error('‚ùå Max retries (5) reached. Job timed out.');
  throw new Error('Job timed out after 5 retries (~10 minutes)');
}

console.log(`üîÑ Retry ${retryCount + 1}/5`);

return [{
  json: {
    ...data,
    retry_count: retryCount + 1
  }
}];
